CREATE TABLE `templates`
(
    `template_id`     int(11) UNSIGNED NOT NULL,
    `member_id`       bigint(20)       NOT NULL,
    `folder_id`       int(11) UNSIGNED                                                               DEFAULT NULL,
    `order`           int(11) UNSIGNED                                                               DEFAULT '0',
    `templates_for`   enum ('Invoice','Payment','General','Password','Request','Prospect','Welcome') DEFAULT 'General',
    `templates_type`  enum ('Email','Letter')                                                        DEFAULT 'Email',
    `name`            char(255)                                                                      DEFAULT NULL,
    `subject`         char(255)                                                                      DEFAULT NULL,
    `from`            char(255)                                                                      DEFAULT NULL,
    `cc`              char(255)                                                                      DEFAULT NULL,
    `bcc`             char(255)                                                                      DEFAULT NULL,
    `message`         longtext,
    `create_date`     date                                                                           DEFAULT NULL,
    `default`         enum ('Y','N')                                                                 DEFAULT 'N',
    `attachments_pdf` tinyint(1)                                                                     DEFAULT '0'
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8 COMMENT ='List of templates that can be used in different situations by company users.';

