            array(
                "faq_id"                => 221,
                "faq_section_id"        => 44,
                "question"              => "How do I monitor my storage and usage in Officio?",
                "answer"                => "<p>To monitor your storage and usage in <em>Officio</em>:</p>\n\n<ol>\n\t<li>Go to <strong>Admin</strong>.</li>\n\t<li>Click on<strong> Company Details</strong>.</li>\n\t<li>Click on <strong>Account Details</strong>.</li>\n\t<li>Review <strong>Free Storage in GB</strong> <strong>Included </strong>and <strong>Storage Used (GB)</strong>.</li>\n</ol>",
                "meta_tags"             => "administration\nplan\nsubscription\ncontrol\nwatch",
                "featured"              => "N",
